//

function removeHtmlTag(strx,chop){
	if(strx.indexOf("<")!=-1)
	{
		var s = strx.split("<");
		for(var i=0;i<s.length;i++){
			if(s[i].indexOf(">")!=-1){
				s[i] = s[i].substring(s[i].indexOf(">")+1,s[i].length);
			}
		}
		strx =  s.join("");
	}
	chop = (chop < strx.length-1) ? chop : strx.length-2;
	while(strx.charAt(chop-1)!=' ' && strx.indexOf(' ',chop)!=-1) chop++;
	strx = strx.substring(0,chop-1);
	return strx+'...';
}
function createSummaryAndThumb(pID){
	var div = document.getElementById(pID);
	var summary = '<p>' + removeHtmlTag(div.innerHTML,summary_noimg) + '</p>';
	div.innerHTML = summary;
}
function dateCorrection(){
	var previousDate;
	meta = document.getElementsByClassName("meta");
	for(var i=0;i<meta.length;i++){
		if(meta[i].innerHTML==""||meta[i].innerHTML=="\n"){
			meta[i].innerHTML = previousDate;
		}else{
			previousDate = meta[i].innerHTML;
		}
	}
}

